import React, { useEffect } from 'react';
import styled from 'styled-components';
import { useGlobalContext } from '../../context/globalContext';
import History from '../../History/History';
import { InnerLayout } from '../../styles/Layouts';
import { Pie } from 'react-chartjs-2';

function Dashboard() {
  const {
    totalExpenses,
    incomes,
    expenses,
    totalIncome,
    totalBalance,
    getIncomes,
    getExpenses,
  } = useGlobalContext();

  useEffect(() => {
    getIncomes();
    getExpenses();
  }, []);

  const getBackgroundColor = (index) => {
    const colors = ['red', 'green', 'blue', 'yellow', 'purple', 'orange'];
    return colors[index % colors.length];
  };

  const aggregateData = (data) => {
    const categories = [];
    const amounts = [];
    const colors = [];

    data.forEach((item, index) => {
      const { category, amount } = item;

      if (categories.includes(category)) {
        const existingIndex = categories.indexOf(category);
        amounts[existingIndex] += amount;
      } else {
        categories.push(category);
        amounts.push(amount);
        colors.push(getBackgroundColor(index));
      }
    });

    return { categories, amounts, colors };
  };

  const aggregateIncome = aggregateData(incomes);
  const aggregateExpenses = aggregateData(expenses);

  const incomeData = {
    labels: aggregateIncome.categories,
    datasets: [
      {
        label: 'Income',
        data: aggregateIncome.amounts,
        backgroundColor: aggregateIncome.colors,
      },
    ],
  };

  const expenseData = {
    labels: aggregateExpenses.categories,
    datasets: [
      {
        label: 'Expenses',
        data: aggregateExpenses.amounts,
        backgroundColor: aggregateExpenses.colors,
      },
    ],
  };

  return (
    <PieChartStyled>
      <InnerLayout>
        <h1>All Transactions</h1>
        <div className="stats-con">
          <div className="chart-con">
            <div className="income-section">
              <PieChart data={incomeData} title="Income" />
            </div>
            <div className="expense-section">
              <PieChart data={expenseData} title="Expenses" />
            </div>
          </div>
        </div>
      </InnerLayout>
    </PieChartStyled>
  );
}

const PieChartStyled = styled.div`
   .stats-con {
    display: flex;
    gap: 2rem;
  }

  .chart-con {
    display: flex;
    gap: 2rem;
    flex-grow: 1;
  }

  .income-section {
    flex-basis: 50%;
    background-color: #f2faf1;
    border: 2px solid #4caf50;
  }

  .expense-section {
    flex-basis: 50%;
    background-color: #fae3e3;
    border: 2px solid #f44336;
  }
`;
const ChartWrapper = styled.div`
  /* ...existing styles... */
  width: 200px; /* Adjust the width to your desired size */
  height: 200px; /* Adjust the height to your desired size */
`;

const PieChart = ({ data, title }) => (
    <div>
      <h2>{title}</h2>
      <Pie
        data={data}
        options={{
          plugins: {
            legend: {
              display: true,
              position: 'bottom',
            },
          },
        }}
      />
    </div>
  );
  
  export default Dashboard;
